<?phpclass VisitorsObject{  // database connection and table name  private $conn;  private $table_visitors = "visitors";  public function __construct()  {    global $db;    //$db = Database::getInstance();    $this->conn = $db;    //$db = new Database;    //exit(0);  }  // read all surveys  public function readAllVisitors()  {    $query = "SELECT * FROM " . $this->table_visitors . " ORDER BY created_at DESC";    $stmt = $this->conn->getData($query);    return $stmt;  }}